
def get_seed(seed=''):
    max_seed = 1000000
    if seed == '':
        return int(random(0, max_seed))
    else:
        try:
            seed = int(seed)
            if seed < max_seed:
                return seed
            else:
                return seed - max_seed
        except:
            seed_list = [ord(x) for x in seed]
            seed = sum(seed_list)
            if seed < max_seed:
                return seed
            else:
                return seed - max_seed
